package days.day1

import days.Day
import react.*


class Day1 : Day() {

    override fun componentWillMount() {
        setState{
            name = "Day 1 - Stairs"
            p1Res = getFinalFloor(input).toString()
            p2Res = getFirstBasement(input).toString()
        }
    }
}

fun RBuilder.day1() = child(Day1::class) {}

fun getFirstBasement(input: String): Int {
    var currentFloor = 0
    for (i in 0 until input.length) {
        when (input[i]) {
            '(' -> currentFloor++
            ')' -> currentFloor--
        }
        if (currentFloor < 0) return i+1
    }
    return -1
}

fun getFinalFloor(input: String): Int {
    return input.count{it == '('} - input.count{it == ')'}
}

